package br.com.javafullstack.logicasCondicionaisRelacionais;

public class estruturaRepeticaoFor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// Estrutura for usado para quando for preciso percorrer uma condição
		
		// Imprimindo de 0 a 10.
		for (int numero = 0; numero <= 10; numero++) {
			System.out.println("Número atual é: " + numero );
		}
		
		
		// Imprimindo de 10 a 0
		for (int number = 10; number >= 0; number--) {
		System.out.println("Número atual é: " + number);
		}

	}

}
